-- Step 1: Insert unique endpoints into EndpointRegistry
MERGE INTO [dbo].[EndpointRegistry] AS target
USING (
    SELECT DISTINCT
        HttpMethod,
        Route,
        EndpointName,
        Description,
        Category,
        IsActive
    FROM EndpointData
) AS source
ON target.HttpMethod = source.HttpMethod AND target.Route = source.Route
WHEN NOT MATCHED THEN
    INSERT (HttpMethod, Route, EndpointName, Description, Category, IsActive, CreatedOn)
    VALUES (source.HttpMethod, source.Route, source.EndpointName, source.Description, source.Category, source.IsActive, GETDATE())
WHEN MATCHED THEN
    UPDATE SET
        EndpointName = source.EndpointName,
        Description = source.Description,
        Category = source.Category,
        IsActive = source.IsActive,
        ModifiedOn = GETDATE();

