INSERT INTO [dbo].[EndpointRolePermission] (EndpointId, RoleName, CreatedOn)
SELECT
    er.EndpointId,
    ed.RoleName,
    GETDATE()
FROM EndpointData ed
INNER JOIN [dbo].[EndpointRegistry] er
    ON ed.HttpMethod = er.HttpMethod
    AND ed.Route = er.Route
WHERE NOT EXISTS (
    SELECT 1
    FROM [dbo].[EndpointRolePermission] erp
    WHERE erp.EndpointId = er.EndpointId
    AND erp.RoleName = ed.RoleName
);

